const container=document.getElementsByClassName("container")[0],windowM=document.getElementsByClassName("modal_window")[0],getPocemons=async()=>{await fetch("https://pokeapi.co/api/v2/pokemon").then(e=>e.json()).then(e=>showPocemons(e))};function showPocemons(e){const t=[];e.results.forEach(e=>{t.push(e.name)});let n=0;for(let e=0;e<20;e+=4){var o=document.createElement("div");o.classList.add("row","my-2");for(let e=0;e<4;e++){var a=document.createElement("div"),c=(a.classList.add("col","my-2"),document.createElement("p"));c.style="margin: 0px;",c.innerHTML=`<a href="#ex1" class="poco" rel="modal:open">${t[n]}</a>`,a.append(c),o.append(a),n++}container.append(o)}document.querySelectorAll(".poco").forEach(e=>{e.addEventListener("click",e=>{windowM.innerText="";e=e.target.innerText;fetch("https://pokeapi.co/api/v2/pokemon/"+e).then(e=>e.json()).then(e=>createInfo(e))})})}function createInfo(e){var t=document.createElement("img"),n=(t.src=e.sprites.front_default,document.createElement("p"));n.innerText=`Вага: ${e.weight} & Зріст: `+e.height,windowM.append(t,n)}getPocemons();{getPocemons};